pragma solidity >=0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../Errors.sol";

abstract contract Pausable is Ownable {
    bool public paused;

    modifier whenNotPaused() {
        if (paused) {
            revert Errors.MangoError(Errors.PAUSED);
        }
        _;
    }

    function pause() external onlyOwner {
        paused = true;
    }

    function unpause() external onlyOwner {
        paused = false;
    }
}
","keccak256":"0x4e8c9e07c8506a8e2638246cee572920bb53e7c1c9191615a4ae644a9d7d64e4","license":"MIT"},"contracts/utils/ReentrancyGuard.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0-only

pragma solidity >=0.8.0;

import "../Errors.sol";

/// @notice Gas optimized reentrancy protection for smart contracts.
/// @author Mango (https://github.com/mango/core/blob/main/contracts/ReentrancyGuard.sol)
/// @author Modified from Solmate (https://github.com/transmissions11/solmate/blob/main/src/utils/ReentrancyGuard.sol)
abstract contract ReentrancyGuard {
    uint256 internal _locked = 1;

    modifier nonReentrant() virtual {
        if (_locked != 1) {
            revert Errors.MangoError(Errors.REENTRANCY);
        }

        _locked = 2;

        _;

        _locked = 1;
    }

    function _initReentrancyGuard() internal {
        _locked = 1;
    }
}
